class StringUtil {

    private static final String SALT = "java";  // 公共的盐值,复杂的情况下盐值也要加密
    private static final int REPEAT = 5;  // 加密次数

    /**
     * 加密处理
     * @parameter 要加密的字符串，需要与盐值整合
     * @return 加密后的数据
     * @date 2019年8月13日
     */
    public static String encode(String str) {
        String temp = str + "{" + SALT + "}";
        byte[] data = temp.getBytes();
        for (int i = 0; i < REPEAT; i++) {
            data = Base64.getEncoder().encode(data);
        }
        return new String(data);
    }

    /**
     * 解密处理
     * @parameter 要解密的内容
     * @return 解密后的原始值
     * @date 2019年8月13日
     */
    public static String decode(String str) {
        byte[] data = str.getBytes();
        for (int i = 0; i < REPEAT; i++) {
            data = Base64.getDecoder().decode(data);
        }
        return new String(data).replaceAll("\\{\\w+\\}", "");
    }

    /**
     * 将图片转换成Base64编码
     * @param imgFile 待处理图片
     * @return Base64编码过的字节数组字符串
     * @date 2019年8月13日
     */
    public static String imageToBase64(String imgFile) {
        InputStream in = null;
        byte[] data = null;

        // 读取图片字节数组
        try {
            in = new FileInputStream(imgFile);

            data = new byte[in.available()];
            in.read(data);
            in.close();
        } catch (IOException e) {
            e.printStackTrace();
        }
        // 对字节数组Base64编码
        Encoder encoder = Base64.getEncoder();

        return String.valueOf(encoder.encode(data));
    }

    /**
     * 在线图片转换成base64字符串
     * @param imgURL 图片线上路径
     * @return
     * @date 2019年8月13日
     */
    public static String imageToBase64ByOnline(String imgURL) {
        ByteArrayOutputStream data = new ByteArrayOutputStream();
        try {
            // 创建URL
            URL url = new URL(imgURL);
            byte[] by = new byte[1024];
            // 创建链接
            HttpURLConnection conn = (HttpURLConnection) url.openConnection();
            conn.setRequestMethod("GET");
            conn.setConnectTimeout(5000);
            InputStream is = conn.getInputStream();
            // 将内容读取内存中
            int len = -1;
            while ((len = is.read(by)) != -1) {
                data.write(by, 0, len);
            }
            // 关闭流
            is.close();
        } catch (IOException e) {
            e.printStackTrace();
        }
        // 对字节数组Base64编码
        Encoder encoder = Base64.getEncoder();
        return String.valueOf(encoder.encode(data.toByteArray()));
    }

    /** 对字节数组字符串进行Base64解码并生成图片
     * @param imgStr 图片数据
     * @param imgFilePath 保存图片全路径地址
     * @return
     */
    public static boolean base64ToImage(String imgStr, String imgFilePath) {
        if (StringUtils.isEmpty(imgStr)) // 图像数据为空
        {
            return false;
        }

        Decoder decoder = Base64.getDecoder();
        try {
            if (imgStr.startsWith("data:image/png;base64,")) {
                imgStr = imgStr.replace("data:image/png;base64,", "");
            } else if (imgStr.startsWith("data:image/jpeg;base64,")) {
                imgStr = imgStr.replace("data:image/jpeg;base64,", "");
            }
            // Base64解码
            byte[] b = decoder.decode(imgStr);
            for (int i = 0; i < b.length; ++i) {
                if (b[i] < 0) { // 调整异常数据
                    b[i] += 256;
                }
            }

            OutputStream out = new FileOutputStream(imgFilePath);
            out.write(b);
            out.flush();
            out.close();

            return true;
        } catch (Exception e) {
            return false;
        }
    }

}
